import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import posts from "../data";

export default function Article() {
  const { id } = useParams();
  console.log(id);
  const [post, setPost] = useState({
    id: "",
    title: "test",
    author: "",
    date: "",
    category: "",
    imageUrl: "",
    imageAlt: "",
    content: "",
  });
  useEffect(() => {
    console.log(posts.find((e) => e.id == id));
    // setPost();
  }, []);
  console.log(post);
  return (
    <>
      <article>
        <h1>{post.title}</h1>
        <p>{post.author}</p>
        <p>{post.date}</p>
        <p>{post.category}</p>
        <img src={post.imageUrl} alt={post.imageAlt} />
        <p>{post.content}</p>
      </article>
    </>
  );
}
