import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import posts from "../data";
import twitter from "../assets/Twitter.png";
import facebook from "../assets/faceBook.png";
import linkedIn from "../assets/linkedIn_social_media_button.png";
import { ShareButtons, ShareCounts, generateShareIcon } from "react-share";

const {
  FacebookShareButton,
  GooglePlusShareButton,
  LinkedinShareButton,
  TwitterShareButton,
  PinterestShareButton,
  VKShareButton,
} = ShareButtons;

export default function Article({ history }) {
  const { id } = useParams();
  const [post, setPost] = useState({});
  const encodedURL = encodeURI(
    `https://bluesmokemedia.com${history.location.pathname}`
  );

  useEffect(() => {
    setPost(posts.find((e) => e.id == id));
  }, []);
  return (
    <>
      <h1>
        Blue Smoke Media
        <br />
        <span className="orange">Nomads</span>
      </h1>
      {post ? (
        <article>
          <img src={post.imageUrl} alt={post.imageAlt} />
          <p className="category">{post.category}</p>
          <h2>{post.title}</h2>
          <p className="author">
            {post.author} / {post.date}
          </p>
          <ul>
            <li>
              <a
                href={`https://facebook.com/sharer/sharer.php?u=${encodedURL}`}
              >
                <img src={facebook} alt="facebook icon" />
              </a>
            </li>
            <li>
              <a
                href="https://twitter.com/share?ref_src=twsrc%5Etfw"
                class="twitter-share-button"
                data-show-count="false"
              >
                <img src={twitter} alt="twitter icon" />
              </a>

              <TwitterShareButton
                url={shareUrl}
                title={title}
                className="shareBtn col-md-1 col-sm-1 col-xs-1"
              >
                <a className="twitter">
                  <i className="fa fa-twitter" aria-hidden="true"></i>
                </a>
              </TwitterShareButton>
            </li>
            <li>
              <img src={linkedIn} alt="LinkedIn icon" />
            </li>
            <li>mail</li>
          </ul>
          <p className="content">{post.content}</p>
          {/* //todo category tags here */}
        </article>
      ) : (
        <p>loading...</p>
      )}
    </>
  );
}
