import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

export default function Search() {
  const [queryType, setQueryType] = useState("");
  const [query, setQuery] = useState();
  const [posts, setPosts] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const excludedKeys = ["id", "imageUrl", "imageAlt"];
  const noResults = () => {
    setFiltered([]);
    document.querySelector(".search ul").style.border = "none";
  };
  useEffect(() => {
    axios
      .get("http://localhost:5000/api/blog")
      .then(({ data }) => {
        document.querySelector(".search ul").style.border = "2px solid black";
        query
          ? setFiltered(
              data.filter((e) => {
                for (var key in e) {
                  if (excludedKeys.indexOf(key) == -1) {
                    if (e[key].toString().toLowerCase().includes(query)) {
                      return e;
                    }
                  }
                }
              })
            )
          : noResults();
      })
      .catch((err) => console.log(err));
  }, [query]);

  // useEffect(() => {
  //   if (query) {
  //     setFiltered(
  //       posts.filter((e) => {
  //         for (var key in e) {
  //           if (excludedKeys.indexOf(key) == -1) {
  //             if (e[key].toString().toLowerCase().includes(query)) {
  //               // console.log(key, e);
  //               return e;
  //             }
  //           }
  //         }
  //       })
  //     );
  //   } else {
  //     setFiltered([]);
  //   }
  // }, [query]);

  return (
    <div className="search">
      <input
        name="search"
        type="text"
        placeholder="Search..."
        onChange={(evt) => setQuery(evt.target.value.toLowerCase())}
      />
      <ul>
        {filtered.map((e) => (
          <Link to={`/article/${e.id}`}>
            <li key={e.id}>
              <h4>{e.title}</h4>
              <span>{e.author}</span>
              <p>{e.snippet.split(" ").splice(0, 20).join(" ")}...</p>
            </li>
          </Link>
        ))}
      </ul>
    </div>
  );
}
